import React from 'react';
import Navbar from './Components/Navbar';
import getUsers from './Utils/API';
import EmployeeCard from "./Components/EmployeeCards";
import './App.css';

class App extends React.Component {
  state = {
    users: [],
    input: '',
    filteredUsers: []
  }

  componentDidMount =()=>{
    getUsers()
    .then(result => {
    this.setState({users: result.data.results, filteredUsers: result.data.results})
    // }).catch(err=>console.log(err))
  }).catch(err=>console.log(err))
}

  handleChange =(e)=>{
    const filtered = this.state.users.filter(user=>user.name.first.toLowerCase().includes(e.target.value) || 
    user.name.last.toLowerCase().includes(e.target.value))
    this.setState({input:e.target.value, filteredUsers:filtered})
  }

  render() {
    return (
      <>
      <Navbar />
      <div className="container">
      <div>
        <div className="row">
          <form className="col s12">
            <div className="row">
              <div className="input-field col s12">
                <textarea id="textarea1" className="materialize-textarea" value={this.state.input} onChange = {this.handleChange}></textarea>
                <label for="textarea1">Search Employee</label>
              </div>
            </div>
          </form>
        </div>
      </div>
      <div className="row">
        <h6 style={{textAlign:"center"}}>Filter Employees: </h6>
      </div>

      <div className="row" >
        <div className="col s1"></div>
        <a className="waves-effect waves-light btn-small col s4">Gender: Male</a>
        <div className="col s2"></div>
        <a className="waves-effect waves-light btn-small col s4">Gender: Female</a>
        <div className="col s1"></div>
      </div>

      <div className="row">
        <a className="waves-effect waves-light btn-small ">Age: 25 and below</a>
        <a className="waves-effect waves-light btn-small ">Age: 26 to 50</a>
        <a className="waves-effect waves-light btn-small ">Age: 50 and above</a>
      </div>

      <div className ="row">
      <EmployeeCard data = {this.state.filteredUsers}/>
      </div>

      </div>
      </>
        );
  }
}

// function App() {
//   return (
//     <Navbar />
//   );
// }

export default App;
